import React, { Component } from 'react'
// import SweetAlert from 'sweetalert2-react';
import SweetAlert from 'react-bootstrap-sweetalert';
// import user from 'admin-lte/dist/img/user2-160x160.jpg'
// import user from 'admin-lte/dist/img/user3-128x128.jpg'
import img from '../../img/profile.jpg';
import axios from 'axios';
import { NotificationContainer, NotificationManager } from 'react-notifications';
import 'react-notifications/lib/notifications.css';
import '../../CSS/style.css';
// import $ from 'jquery';
import "croppie/croppie.css"
import { SessionContext } from "../../Context/Session";

import "croppie/croppie.css";
import { Croppie } from "croppie";

const userInfo = JSON.parse(localStorage.getItem('user-info'))
axios.interceptors.request.use(
    config => {
        config.headers.authorization = `Bearer ${userInfo.user.token}`;
        return config;
    },
    error => {
        return Promise.reject(error)
    }
)

const croppieOptions = {
  showZoomer: true,
  enableOrientation: true,
  mouseWheelZoom: "ctrl",
  viewport: {
    width: 300,
    height: 200,
    type: "square"
  },
  boundary: {
    width: "50vw",
    height: "50vh"
  }
};
const croppie = document.getElementById("croppie");
const c = new Croppie(croppie, croppieOptions);


// const $ = window.$;



export default class About extends Component {
    static contextType = SessionContext;
    constructor(props) {
        super(props);
        const userInfo = localStorage.getItem('user-info')
        const user = JSON.parse(userInfo)
        this.state = {
            status: ['Passive', 'Active', 'Left his job', 'On vacation', 'Was fired', 'Is engaged in', 'Graduated from training', 'Will continue',],
            img: user.user.user_info.image,
            show: false,
            changePassword: false,
            formData: {},
            delete: false,
            croppedImage: null,
            isFileUploaded: false

        }
    }

    file = React.createRef();
    croppie = React.createRef();
    img = React.createRef();

    
    date(value) {
        let date = value.split('-')
        return `${date[2]}/${date[1]}/${date[0]}`
    }
    onConfirm = async (link) => {

        let formData = new FormData();
        this.state.formData.file && formData.append("file", this.state.formData.file)
        this.state.formData.old_password && formData.append("old_password", this.state.formData.old_password)
        this.state.formData.confirm_password && formData.append("confirm_password", this.state.formData.confirm_password)
        this.state.formData.new_password && formData.append("new_password", this.state.formData.new_password)

        formData.append("id", userInfo.user.user_id)

        let response = await axios.post(`http://127.0.0.1:8000/api/managers/manager/${link}`, formData)

        console.log(response.data)

        if (response.data.status === 'success') {
            NotificationManager.success('Məlumatlar dəyişdirildi.', 'Success', 5000);
            if (response.data.user) {
                userInfo['user']['user_info'] = response.data.user;
                localStorage.setItem('user-info', JSON.stringify(userInfo))
                this.context.setSession(response.data.status, userInfo)
                this.setState({ img: response.data.image, show: false, formData: {} })
            }
        }
        if (response.data.status === 'error') {
            let message = response.data.message;
            for (const [key, value] of Object.entries(message)) {
                NotificationManager.error(value, 'Error', 5000);
            }
            // this.setState({ formData: {} })
        }
    }
    onCancel = (st) => {
        this.setState({ [st]: false, formData: {} })
    }
    onDelete = async () => {


        let response = await axios.post(`http://127.0.0.1:8000/api/managers/manager/delete_image/${userInfo.user.user_id}`)
        if (response.data.status === 'success') {
            NotificationManager.success('Məlumatlar dəyişdirildi.', 'Success', 5000);
            userInfo['user']['user_info'] = response.data.user;
            localStorage.setItem('user-info', JSON.stringify(userInfo))
            this.context.setSession(response.data.status, userInfo)
            this.setState({ img: null, delete: false, formData: {} })
        }
        if (response.data.status === 'error') {
            let message = response.data.message;
            for (const [key, value] of Object.entries(message)) {
                NotificationManager.error(value, 'Error', 5000);
            }
            this.setState({ formData: {} })
        }
    }
    changeImage(event) {

        let newFormData = this.state.formData;
        newFormData['file'] = event.target.files[0]
        this.setState({
            formData: newFormData
        })
    }
    changeData(event) {

        let newFormData = this.state.formData;
        newFormData[event.target.name] = event.target.value
        this.setState({
            formData: newFormData
        })
    }

    componentDidMount = () => {
        var image_crop = document.getElementById('image_demo').croppie({
            viewport: {
                width: 500,
                height: 271,
                type: 'square'
            },
            boundary: {
                width: 510,
                height: 281,
            }
        });

        image_crop.croppie('bind', {
            url: "{{ asset('img/blog/' . $flight->blog_image) }}",
        });

        let url = false;
    }

    coverImageChange = () => {
        var reader = new FileReader();
        reader.onload = function (event) {
            this.image_crop.croppie('bind', {
                url: event.target.result,
            });
            this.url = true
        }
        reader.readAsDataURL(this.files[0]);
    }

    onSubmit = (event) => {
        event.preventDefault()
        let id =  userInfo.user.user_id
        let formData = new FormData();
        formData.append('id', id);


        this.image_crop.croppie('result', {
            type: 'blob',
            format: 'webp',
            size: {
                width: 1170,
                height: 635
            },
            quality: 1
        }).then(function (blob) {
            if (this.url) {
                let newFormData = this.state.formData;
                newFormData['file'] = blob
                this.setState({
                    formData: newFormData
                })
            }
        });
    }
    render() {
        return (
            <>
                <NotificationContainer />
                <div className="col-lg-3 float-left">
                    <div className="row">
                        <div className="card col-12">
                            <div className="card-header">
                                <div className="row justify-content-center profile_image">
                                    <div className="rounded-circle actions">
                                        <button className="btn btn-success" onClick={() => this.setState({ show: true })}><i className="fa fa-plus"></i></button>
                                        <button className="btn btn-danger ml-1" onClick={() => this.setState({ delete: true })} ><i className="fa fa-trash"></i></button>
                                    </div>
                                    <SweetAlert
                                        title={"Choose a new picture"}
                                        onConfirm={() => this.onConfirm('add_image')}
                                        onCancel={() => this.onCancel('show')}
                                        showCancel={`true`}
                                        show={this.state.show}
                                        confirmBtnBsStyle="success"
                                        confirmBtnText="Save"
                                        cancelBtnBsStyle="danger"
                                        dependencies={[this.state.image]}
                                    >
                                        {() => (

                                            <div className="from-group">
                                                <div className="col-md-12">
                                                    <div id="image_demo"></div>
                                                </div>
                                                <div className="input-group" style="margin-top: 15px">
                                                    <div className="custom-file">
                                                        <input
                                                            type={'file'}
                                                            className="form-control p-0"
                                                            onChange={this.changeImage.bind(this)}
                                                        // onChange={(e) => this.setState({ formData: e.target.files })}
                                                        />
                                                        <input type="file" accept="image/*" id="cover_image" />
                                                    </div>
                                                </div>
                                            </div>
                                        )}
                                    </SweetAlert>

                                    <SweetAlert
                                        title={"Are you sure?"}
                                        placeholder={"You won't be able to revert this!"}
                                        type={`warning`}
                                        onConfirm={this.onDelete}
                                        onCancel={() => this.setState({ delete: false })}
                                        showCancel={`true`}
                                        show={this.state.delete}
                                        confirmBtnBsStyle="success"
                                        confirmBtnText="Yes, delete it!"
                                        cancelBtnBsStyle="danger"
                                        cancelBtnText='No, cancel!'
                                    />
                                    <img src={this.state.img ? this.state.img : img} style={{ width: '150px', height: '150px' }} className="rounded-circle" alt="user" />
                                </div>
                            </div>
                            <div className="card-body">
                                <div className="row">
                                    <label className="col-12">Main info</label>
                                </div>
                                <div className="row">
                                    <p className="col-12 m-0">Status</p>
                                    <p className="col-12"><a href="#" onClick={(e) => e.preventDefault()} className="Dashed">{(this.props.user.status !== '') ? this.state.status[this.props.user.status] : `[No]`}</a></p>
                                </div>
                                <div className="row">
                                    <p className="col-12 m-0">Age</p>
                                    <p className="col-12">{(this.props.user.date) ? this.date(this.props.user.date) : ''}</p>
                                </div>
                                <div className="row">
                                    <p className="col-12 m-0">Office</p>
                                    <p className="col-12">{this.props.office && this.props.office.name}</p>
                                </div>
                                <div className="row">
                                    <p className="col-12 m-0">Email</p>
                                    <p className="col-12">{this.props.user.email}</p>
                                </div>
                                <div className="row">
                                    <p className="col-12 m-0">Mobile</p>
                                    <p className="col-12">{this.props.user.mobile}</p>
                                </div>
                                <div className="row">
                                    <p className="col-12 m-0">Phone</p>
                                    <p className="col-12">{this.props.user.phone}</p>
                                </div>
                                <div className="row">
                                    <SweetAlert
                                        title={"Choose a new picture"}
                                        onConfirm={() => this.onConfirm('change_password')}
                                        onCancel={() => this.onCancel('changePassword')}
                                        showCancel={`true`}
                                        show={this.state.changePassword}
                                        confirmBtnBsStyle="success"
                                        confirmBtnText="Save"
                                        cancelBtnBsStyle="danger"
                                    >
                                        {() => (
                                            <div className="col-12">
                                                <div className="form-group">
                                                    <input
                                                        type={'password'}
                                                        className="form-control"
                                                        name="old_password"
                                                        value={this.state.formData.old_password}
                                                        onChange={this.changeData.bind(this)}
                                                    />
                                                </div>
                                                <div className="form-group">
                                                    <input
                                                        type={'password'}
                                                        className="form-control"
                                                        name="new_password"
                                                        value={this.state.formData.new_password}
                                                        onChange={this.changeData.bind(this)}
                                                    />
                                                </div>
                                                <div className="form-group">
                                                    <input
                                                        type={'password'}
                                                        className="form-control"
                                                        name="confirm_password"
                                                        value={this.state.formData.confirm_password}
                                                        onChange={this.changeData.bind(this)}
                                                    />
                                                </div>
                                            </div>
                                        )}
                                    </SweetAlert>
                                    <p className="col-12"><a href="#" onClick={(e) => { e.preventDefault(); this.setState({ changePassword: true }); }} className="Dashed">Change password</a></p>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </>
        )
    }
}
