import React, { Component } from 'react'
import ReactTooltip from 'react-tooltip';
import { Link } from "react-router-dom";
import axios from 'axios';
import img from '../../img/profile.jpg';

export default class Messages extends Component {
    constructor(props) {
        super(props)
        this.state = { messages: {}, myid: 0};
    }
    componentDidMount = () => {
        this.load()
    }
    load = async name => {
        let userInfo = JSON.parse(localStorage.getItem('user-info'))
        this.setState({myid: userInfo.user.user_info.id})
        axios.interceptors.request.use(
            config => {
                config.headers.authorization = `Bearer ${userInfo.user.token}`;
                return config;
            },
            error => {
                return Promise.reject(error)
            }
        )
        let response = await axios.get(`http://127.0.0.1:8000/api/managers/messages`)
        console.log(response.data)
        if(response.data.status === 'success'){
            this.setState({
                messages: response.data.messages
            })
        }

        
        
    }
    render() {
        const array = []
        return (
            <div className="row">
                <div className="col-12">
                    <div className="row">
						<div className="col-12">
							<h4>Messages</h4>
						</div>
					</div>
                    <div className="row">
                        <div className="col-12">
                        {this.state.messages.length}
                            <div className="table-responsive bg-white m-0 p-3 rounded shadow">
                                <table className="table table-bordered m-0">
                                    <tbody>
                                    {this.state.messages.length > 0 ? () => {
                                        
                                        if(this.state.messages.sender !== this.state.myid){
                                            let status = this.state.messages.status ? 1 : 0;
                                            if(!array.includes(this.state.messages.sender)){
                                                array.push(this.state.messages.sender)
                                                return (
                                                    <tr className={status ? '' : 'bg-warning'}>
                                                        <td>
                                                            <img className="rounded-circle h-100 w-100" style={{maxWidth: '60px', maxHeight: '60px'}} src={this.state.messages.user1_image ? this.state.messages.user1_image : img} alt='account' />
                                                            {this.state.messages.image}
                                                        </td>
                                                        <td>
                                                        <Link to={`/MessagesUser?user=${this.state.messages.sender}`}>{this.state.messages.user_1}</Link>
                                                        </td>
                                                        <td>
                                                            {this.state.messages.created_at}
                                                        </td>
                                                        <td>
                                                            {status}
                                                        </td>
                                                        <td className="btnTD text-center">
                                                            <Link to={`/MessagesUser?user=${this.state.messages.sender}`} className="btn Btn32 btn-success" ref={ref => this.fooRef = ref} data-tip='Write a message...'>
                                                                <i className="fa fa-comment"></i>
                                                            </Link>
                                                            <ReactTooltip />

                                                            {/* <button className="btn Btn32 btn-danger" data-id={this.state.messages.id} data-link="lesson" onClick={this.delete}><i className="fas fa-trash"></i></button> */}

                                                        </td>
                                                    </tr>
                                                )
                                                
                                            }
                                        }
                                        else if(this.state.messages.receiving !== this.state.myid){
                                            let status = this.state.messages.status ? 1 : 0;
                                            if(!array.includes(this.state.messages.receiving)){
                                                array.push(this.state.messages.receiving)
                                                return (
                                                    <tr className={status ? '' : 'bg-warning'}>
                                                        <td>
                                                            <img className="rounded-circle h-100 w-100" style={{maxWidth: '60px', maxHeight: '60px'}} src={this.state.messages.user2_image ? this.state.messages.user2_image : img} alt='account' />
                                                            {this.state.messages.image}
                                                        </td>
                                                        <td>
                                                            <Link to={`/MessagesUser?user=${this.state.messages.receiving}`}>{this.state.messages.user_2}</Link>
                                                        </td>
                                                        <td>
                                                            {this.state.messages.created_at}
                                                        </td>
                                                        <td>{status}</td>
                                                        <td className="btnTD text-center">
                                                            <Link to={`/MessagesUser?user=${this.state.messages.receiving}`} className="btn Btn32 btn-success" ref={ref => this.fooRef = ref} data-tip='Write a message...'>
                                                                <i className="fa fa-comment"></i>
                                                            </Link>
                                                            <ReactTooltip />

                                                            {/* <button className="btn Btn32 btn-danger" data-id={this.state.messages.id} data-link="lesson" onClick={this.delete}><i className="fas fa-trash"></i></button> */}

                                                        </td>
                                                    </tr>
                                                )
                                                
                                            }
                                            
                                        }

                                                    
                                                } :  
                                                    <tr>
                                                        <td colSpan="12" className="text-center">
                                                            Empty
                                                        </td>
                                                    </tr>
                                                 }
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
