import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { NotificationContainer, NotificationManager } from 'react-notifications';
import 'react-notifications/lib/notifications.css';
import { SessionContext } from "../../Context/Session";

import "croppie/croppie.css";
// import { Button, Grid } from "@material-ui/core"
import Croppie from "croppie";


class ImageUpload extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            croppie: null,
            image: ''

        }
    }

    componentDidMount = () => {

        var c = new Croppie(document.getElementById('image-helper'), {
            viewport: {
                width: 500,
                height: 271,
                type: 'square'
            },
            boundary: {
                width: 510,
                height: 281,
            }
        });

        this.setState({croppie: c})
    }
    handleImage = (image) => {
        this.setState({})
        const el = document.getElementById("image-helper")
        if (el) {
          const croppieInstance = new Croppie(el, {
            enableExif: true,
            viewport: {
              height: 250,
              width: 250,
            },
            boundary: {
              height: 280,
              width: 400,
           }
        });
        croppieInstance.bind({
          url: image,
        });
        setCroppie(croppieInstance)
        }
      }
    render() {
        return (
            <>
                <div class="modal fade bd-example-modal-lg" id="imageUpload" tabindex="-1" role="dialog" aria-labelledby="imageUploadLabel" aria-hidden="true">
                    <div class="modal-dialog modal-lg" role="document">
                        <form onSubmit={``}>
                            <div class="modal-content">
                                <div class="modal-header">
                                    <h5 class="modal-title" id="imageUploadLabel">Edit</h5>
                                    <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                                        <span aria-hidden="true">&times;</span>
                                    </button>
                                </div>
                                <div class="modal-body">
                                    <div id="image-helper"></div>
                                    <input type="file" accept="image/*" id="cover_image"></input>
                                </div>
                                <div class="modal-footer">
                                    <button type="reset" class="btn btn-secondary" data-dismiss="modal">Close</button>
                                    <button type="submit" class="btn btn-primary">Save changes</button>
                                </div>
                            </div>
                        </form>
                    </div>
                </div>
            </>

        );
    }
}

export default ImageUpload;
