import React, { Component } from 'react'
import AddOfficess from './Modals/AddOffices'
import axios from 'axios';
import { NotificationContainer, NotificationManager } from 'react-notifications';
import 'react-notifications/lib/notifications.css';


export default class Offices extends Component {
    constructor(props) {
        super(props)
        this.state = {
            edit: 0,
            offices: [],
            data: {name: '', address: '', mobile: '', phone: '', email: '', classrooms: '', capacity: '', note: ''}
        }
      }
      componentDidMount = () => {
        this.load()
    }
    setData = (name, value) => {
        let newData = this.state.data;
        newData[name] = value
        
        this.setState({
            data: newData
        })
    }
    load = async name => {
        let userInfo = JSON.parse(localStorage.getItem('user-info'))
        axios.interceptors.request.use(
            config => {
                config.headers.authorization = `Bearer ${userInfo.user.token}`;
                return config;
            },
            error => {
                return Promise.reject(error)
            }
        )
        let response = await axios.get(`${process.env.REACT_APP_API_URL}/managers/offices`)
        if(response.data.status === 'success'){
            this.setState({
                offices: response.data.offices,
                delete: userInfo.user.user_info.type === 1 ? true : false
            })
        }
        
    }
    delete = async id => {
        let userInfo = JSON.parse(localStorage.getItem('user-info'))
        axios.interceptors.request.use(
            config => {
                config.headers.authorization = `Bearer ${userInfo.user.token}`;
                return config;
            },
            error => {
                return Promise.reject(error)
            }
        )
        let response = await axios.delete(`${process.env.REACT_APP_API_URL}/managers/offices/${id}`)


        if (response.data.status === 'success') {
            NotificationManager.warning(response.data.message, 'Warning', 5000);
            this.load()
        }
    }
    render() {
        return (
            <>
                        <NotificationContainer />
            <div className="row">
                <div className="col-12">
                    <div className="row">
                        <div className="col-12 col-sm-6">
                            <h4>Offices</h4>
                        </div>
                        <div className="col-12 col-sm-6 clearfix">
                            <button type="button" class="btn btn-info  float-right" data-toggle="modal" data-target="#exampleModal" onClick={() => this.setState({edit: 0, data: {name: '', address: '', mobile: '', phone: '', email: '', classrooms: '', capacity: '', note: ''}})} data-whatever="@getbootstrap" >Add</button>
                        </div>
                    </div>
                    <div className="row mt-3">
                        <div className="col-12">
                        <div className="loading" style={{ display: this.state.display ? 'block' : 'none' }}>
                            <div className="text-center">
                                <span>
                                    Loading...
                                </span>
                            </div>
                        </div>
                            <div className="table-responsive bg-white m-0 p-3 rounded shadow">
                                <table class="table table-bordered m-0">
                                    <thead>
                                        <tr>
                                            <th scope="col">Name</th>
                                            <th scope="col">Address</th>
                                            <th scope="col">Contacts</th>
                                            <th scope="col">Classrooms</th>
                                            <th scope="col">Capacity</th>
                                            <th scope="col">Note</th>
                                            <th scope="col">Status</th>
                                            <th scope="col"></th>
                                            {/* <th scope="col"><button className="btn Btn32 text-danger"><i class="fas fa-trash"></i></button></th> */}
                                        </tr>
                                    </thead>
                                    <tbody>
                                    {this.state.offices.length > 0 ? this.state.offices.map((value, index) => {
                                                    return (
                                                        <tr key={index}>
                                                            <td>
                                                                {value.name}
                                                            </td>
                                                            <td>
                                                                <p className="m-0 p-0"><i>{value.address}</i></p>
                                                            </td>
                                                            <td>
                                                                <p className="m-0 p-0">{value.mobile}</p>
                                                                <p className="m-0 p-0">{value.phone}</p>
                                                                <p className="m-0 p-0">{value.email}</p>
                                                            </td>
                                                            <td>
                                                                {value.classrooms}
                                                            </td>

                                                            <td>
                                                                {value.capacity}
                                                            </td>
                                                            <td>
                                                                {value.note}
                                                            </td>
                                                            <td>
                                                                {value.status}
                                                            </td>
                                                            <td className="btnTD text-center">
                                                                <button className="btn Btn32 btn-warning" data-toggle="modal" data-target="#exampleModal" onClick={() => this.setState({edit: value.id, data: {name: value.name, address: value.address,  mobile: value.mobile, email: value.email, phone: value.phone,  capacity: value.capacity, classrooms: value.classrooms, note: value.note}})}><i class="fas fa-pencil-alt"></i></button>
                                                                {
                                                                    this.state.delete ? <button className="btn Btn32 btn-danger" onClick={() => this.delete(value.id)}><i class="fas fa-trash"></i></button> : ''
                                                                }
                                                            </td>
                                                        </tr>
                                                    )
                                                }) :  
                                                    <tr>
                                                        <td colSpan="12" className="text-center">
                                                            Empty
                                                        </td>
                                                    </tr>
                                                 }
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <AddOfficess edit={this.state.edit} data={this.state.data} load={this.load} setData={this.setData} />
            </>
        )
    }
}
