import React, { Component } from 'react'
import axios from 'axios';
import { NotificationContainer, NotificationManager } from 'react-notifications';
import 'react-notifications/lib/notifications.css';
import { SessionContext } from "../../Context/Session";
import $ from 'jquery';
import '../../CSS/style.css';
import SweetAlert from 'react-bootstrap-sweetalert';


export default class Notifications extends Component {
    constructor(props) {
        super(props)
        this.state = {
            notifications: [],
        }
    }
    load = async name => {
        let userInfo = JSON.parse(localStorage.getItem('user-info'))
        axios.interceptors.request.use(
            config => {
                config.headers.authorization = `Bearer ${userInfo.user.token}`;
                return config;
            },
            error => {
                return Promise.reject(error)
            }
        )
        let response = await axios.get(`http://127.0.0.1:8000/api/managers/notifications`)
        if (response.data.status === 'success') {
            this.setState({
                leads: response.data.notifications,
            })
        }

    }
    render() {
        return (
            <>
                <NotificationContainer />
                <div className="row">
                    <div className="col-12">
                        <div className="row">
                            <div className="col-12 col-sm-6">
                                <h4>Notifications</h4>
                            </div>
                        </div>
                        <div className="row mt-3">
                            <div className="col-12">
                                <div className="table-responsive bg-white m-0 p-3 rounded shadow">
                                    <table className="table table-bordered m-0">
                                        <thead>
                                            <tr>
                                                <th scope="col">Content</th>
                                                <th scope="col">From</th>
                                                <th scope="col">Created time</th>
                                                <th scope="col"></th>
                                                {/* <th scope="col"><button className="btn Btn32 text-danger"><i className="fas fa-trash"></i></button></th> */}
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {this.state.notifications.length > 0 ? this.state.notifications.map((value, index) => {
                                                return (
                                                    <tr key={index}>
                                                        <td>
                                                            {value.content}
                                                        </td>
                                                        <td>
                                                            {value.from}
                                                        </td>
                                                        <td>
                                                            {value.created_at}
                                                        </td>
                                                        <td className="btnTD text-center">
                                                            <Link className="nav-link" to="Messages">
                                                                <i className="far fa-comments"></i>
                                                                <span className="badge badge-danger navbar-badge">{this.state.messages}</span>
                                                            </Link>
                                                        </td>
                                                    </tr>
                                                )
                                            }) :
                                                <tr>
                                                    <td colSpan="12" className="text-center">
                                                        Empty
                                                    </td>
                                                </tr>
                                            }
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </>
        )
    }
}
