import React, { Component } from 'react'
import { Link } from "react-router-dom";
import AddAdministrativeStaff from './Modals/AddAdministrativeStaff'
import axios from 'axios';
import img from '../../img/profile.jpg';
import { NotificationContainer, NotificationManager } from 'react-notifications';
import 'react-notifications/lib/notifications.css';
import Datatables from '../Datatables';


export default class AdministrativeStaff extends Component {
    constructor(props) {
        super(props)
        this.state = {
            managers: [],
            display: true
        }
    }
    componentDidMount = () => {
        this.load()
    }
    removeComponent = () => {
        this.setState({
            modal: []
        })
    }
    addComponent = (value) => {
        let edit = 0
        let data = {}
        if (value) {
            edit = value.id
            data = value
        }

        let modal = <AddAdministrativeStaff edit={edit} data={data} load={this.load} removeComponent={this.removeComponent} />

        this.setState({
            modal: modal
        })
    }
    load = async name => {
        let userInfo = JSON.parse(localStorage.getItem('user-info'))
        axios.interceptors.request.use(
            config => {
                config.headers.authorization = `Bearer ${userInfo.user.token}`;
                return config;
            },
            error => {
                return Promise.reject(error)
            }
        )
        let response = await axios.get(`${process.env.REACT_APP_API_URL}/managers/manager`)
        if (response.data.status === 'success') {
            this.setState({
                managers: response.data.managers,
                delete: userInfo.user.user_info.type === 1 ? true : false,
                display: false
            })
        }
    }
    delete = async id => {
        let userInfo = JSON.parse(localStorage.getItem('user-info'))
        axios.interceptors.request.use(
            config => {
                config.headers.authorization = `Bearer ${userInfo.user.token}`;
                return config;
            },
            error => {
                return Promise.reject(error)
            }
        )
        let response = await axios.delete(`${process.env.REACT_APP_API_URL}/managers/manager/${id}`)


        if (response.data.status === 'success') {
            NotificationManager.warning(response.data.message, 'Warning', 5000);
            this.load()
        }
    }
    buttons = (row) => {
        return (
            <div style={{ minWidth: '380px' }}>
                <Link to={`/Messages/Chat/${row.id}`} className="btn Btn32 btn-success"><i className="fas fa-comment"></i></Link>
                <button className="btn Btn32 btn-warning" data-toggle="modal" data-target="#exampleModal" onClick={() => this.addComponent(row.value)}><i className="fas fa-pencil-alt"></i></button>
                <button className="btn Btn32 btn-primary"><i className="fas fa-calendar-alt"></i></button>
                { this.state.delete ? <button className="btn Btn32 btn-danger" onClick={() => this.delete(row.id)}><i class="fas fa-trash"></i></button> : '' }
            </div>
        )
    }                   
    render() {
        const role = ['', 'Manager', 'Teacher', 'Student', 'Coordinator']
        const status = ['Resigned', 'Works', 'Intern', 'On Holiday']
        const columns = [
            {
                name: 'Image',
                selector: row => row.image,
            },
            {
                name: 'Full name',
                sortable: true,
                selector: row => row.name,
            },
            {
                name: 'Mobile',
                selector: row => row.mobile,
            },
            {
                name: 'Address',
                selector: row => row.address,
            },
            {
                name: 'Email',
                selector: row => row.email,
            },
            {
                name: 'Lessons',
                selector: row => row.lessons,
            },
            {
                name: 'Levels',
                sortable: true,
                selector: row => row.levels,
            },
            {
                name: 'Office',
                selector: row => row.office,
            },
            {
                name: 'Status',
                selector: row => row.status,
            },
            {
                name: 'Created at',
                sortable: true,
                selector: row => row.created_at,
            },
            {
                name: '',
                selector: row => this.buttons(row),
            },
        ];
        const data = []
        if(this.state.managers.length > 0)
        {
            this.state.managers.map((value, index) => {
                const lessons = JSON.parse(value.lessons1)
                const levels = JSON.parse(value.levels1)
                data['lessons'] = lessons
                data['levels'] = levels
                const status = ['Resigned', 'Works', 'Intern', 'On Holiday']
                const les = []
                const lev = []
                lessons.map((v, i) => {
                    les.push(<p className="m-0">{v.label}</p>)
                })
                levels.map((v, i) => {
                    lev.push(<p className="m-0">{v.label}</p>) 
                })
                data.push({ 
                    id: value.id, 
                    value: value, 
                    image: <img className="rounded-circle h-100 w-100" style={{ maxWidth: '100px', maxHeight: '100px' }} src={value.image ? process.env.REACT_APP_URL + '/' + value.image : img} alt='account' />,
                    title: value.first_name + ' ' +value.last_name, 
                    name: <Link to={`/User/${value.id}`} >{value.first_name} {value.last_name}</Link>, 
                    mobile: value.mobile, 
                    address:  value.address,  
                    email: value.email, 
                    levels: lev, 
                    lessons: les, 
                    office: value.office_name, 
                    status: status[value.status], 
                    created_at: value.created_at,
                })
            })
        }
        return (
            <>
            <NotificationContainer />
                <div className="row">
                    <div className="col-12">
                        <div className="row">
                            <div className="col-12 col-sm-6">
                                <h4>Adminitrative staff</h4>
                            </div>
                            <div className="col-12 col-sm-6 clearfix">
                                <button type="button" class="btn btn-info  float-right" data-toggle="modal" data-target="#exampleModal" onClick={() => this.addComponent()}>Add</button>
                            </div>
                        </div>
                        <div className="row mt-3">
                            <div className="col-12">
                            <div className="loading" style={{ display: this.state.display ? 'block' : 'none' }}>
                            <div className="text-center">
                                <span>
                                    Loading...
                                </span>
                            </div>
                        </div>
                                <div className="bg-white m-0 p-3 rounded shadow">
                                <Datatables columns={columns} data={data} pending={this.state.display} filter={`title`}/>
                                    <table class="table table-bordered m-0">
                                        <thead>
                                            <tr>
                                                <th scope="col"></th>
                                                <th scope="col">Full name</th>
                                                <th scope="col">Role</th>
                                                <th scope="col">Officess</th>
                                                <th scope="col">Status</th>
                                                <th scope="col">Contacts</th>
                                                <th scope="col"></th>
                                                {/* <th scope="col"><button className="btn Btn32 text-danger"><i class="fas fa-trash"></i></button></th> */}
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {this.state.managers.length > 0 ? this.state.managers.map((value, index) => {
                                                return (
                                                    <tr key={index}>
                                                        <td>
                                                            <img className="rounded-circle h-100 w-100" style={{ maxWidth: '100px', maxHeight: '100px' }} src={value.image ? process.env.REACT_APP_URL + '/' + value.image : img} alt='account' />
                                                        </td>
                                                        <td>
                                                            <Link to={`/User/${value.id}`} >{value.first_name} {value.last_name}</Link>
                                                        </td>
                                                        <td>
                                                            {role[value.type]}
                                                        </td>
                                                        <td>
                                                            {value.name}
                                                        </td>
                                                        <td>
                                                            {status[value.status]}
                                                        </td>
                                                        <td>
                                                            <p className="m-0 p-0">{value.mobile}</p>
                                                            <p className="m-0 p-0">{value.phone}</p>
                                                            <p className="m-0 p-0">{value.email}</p>
                                                            <p className="m-0 p-0"><i>{value.address}</i></p>
                                                        </td>
                                                        <td className="btnTD text-center">
                                                            <Link to={`/MessagesUser/${value.id}`} className="btn Btn32 btn-success mx-1"><i class="fas fa-comment"></i></Link>
                                                            <button className="btn Btn32 btn-warning mx-1" data-toggle="modal" data-target="#exampleModal" onClick={() => this.addComponent(value)}><i class="fas fa-pencil-alt"></i></button>
                                                            {
                                                                this.state.delete ? <button className="btn Btn32 btn-danger" onClick={() => this.delete(value.id)}><i class="fas fa-trash"></i></button> : ''
                                                            }
                                                        </td>
                                                    </tr>
                                                )
                                            }) :
                                                <tr>
                                                    <td colSpan="12" className="text-center">
                                                        Empty
                                                        </td>
                                                </tr>
                                            }
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                {
                    this.state.modal
                }
            </>
        )
    }
}
